import React from "react";
// recharts
import { PieChart, Pie, Sector, Cell, ResponsiveContainer } from "recharts";
// データを用意する。
const data = [
  { name: "ゴルフ", value: 400 },
  { name: "サッカー", value: 300 },
  { name: "テニス", value: 200 },
  { name: "野球", value: 100 },
];
// カラーを用意する。
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];
// ラベルの位置や内容をカスタマイズ
const renderLabel = ({ name }: any) => {
  return name;
};

const WorkTimePieChart = (): JSX.Element => {
  return (
    <div>
      <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
      }}
      >
      <h2>あなたの勤務時間割合</h2>
      </div>
      <ResponsiveContainer width="80%" height={400}>

        <PieChart width={500} height={400}>
          <Pie
            data={data}
            cx="50%"
            cy="50%"
            labelLine={true}
            outerRadius={150}
            fill="#8884d8"
            nameKey="name"
            dataKey="value"
            label={renderLabel}
          >
            {data.map((entry, index) => (
              <Cell
                key={`cell-${index}`}
                fill={COLORS[index % COLORS.length]}
              />
            ))}
          </Pie>
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
};
export default WorkTimePieChart;
