import React from "react";
import {
  Bar,
  Line,
  ComposedChart,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from "recharts";

const dailyData = [
  {
    date: "7/23",
    "4. とても満足": 40,
    "3. 満足": 24,
    "2. 不満": 24,
    "1. とても不満": 15,
    平均スコア: 4.1
  },
  {
    date: "7/24",
    "4. とても満足": 30,
    "3. 満足": 13,
    "2. 不満": 22,
    "1. とても不満": 30,
    平均スコア: 3.2
  },
  {
    date: "7/25",
    "4. とても満足": 20,
    "3. 満足": 98,
    "2. 不満": 22,
    "1. とても不満": 30,
    平均スコア: 2.3
  },
  {
    date: "7/26",
    "4. とても満足": 27,
    "3. 満足": 39,
    "2. 不満": 20,
    "1. とても不満": 18,
    平均スコア: 2.7
  },
  {
    date: "7/26",
    "4. とても満足": 18,
    "3. 満足": 48,
    "2. 不満": 21,
    "1. とても不満": 20,
    平均スコア: 1.8
  },
  {
    date: "7/27",
    "4. とても満足": 23,
    "3. 満足": 38,
    "2. 不満": 25,
    "1. とても不満": 3,
    平均スコア: 2.3
  },
  {
    date: "7/28",
    "4. とても満足": 34,
    "3. 満足": 43,
    "2. 不満": 21,
    "1. とても不満": 30,
    平均スコア: 3.4
  }
];

// カラーを用意する。
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];
// ラベルの位置や内容をカスタマイズ
const renderLabel = ({ name }: any) => {
  return name;
};

const WorkTimeBarChart = (): JSX.Element => {
  return (
    <div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}
        >
        <h2>あなたのワークスタイル</h2>
      </div>

        <ComposedChart width={1000} height={200} data={dailyData}>
          <XAxis dataKey="date" padding={{ right: 50, left: 50 }} />
          <YAxis yAxisId={1} label={{ value: "回答数", angle: -90, dx: -20 }} />
          <YAxis
            yAxisId={2}
            orientation="right"
            domain={[0, 5]}
            tickCount={6}
            label={{ value: "平均スコア", angle: -90, dx: 20 }}
          />
          <Tooltip />
          <Legend />
          <CartesianGrid strokeDasharray="3 3" />
          <Bar
            yAxisId={1}
            dataKey="1. とても不満"
            stackId="a"
            barSize={50}
            fill="#FF8042"
          />
          <Bar yAxisId={1} dataKey="2. 不満" stackId="a" fill="#FFBB28" />
          <Bar yAxisId={1} dataKey="3. 満足" stackId="a" fill="#00C49F" />
          <Bar yAxisId={1} dataKey="4. とても満足" stackId="a" fill="#0088FE" />
          <Line yAxisId={2} dataKey="平均スコア" />
        </ComposedChart>

    </div>
  );
};
export default WorkTimeBarChart;
