import React from "react";
import {
  Bar, Line, ComposedChart, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from "recharts";

import pwInfoRegular from "../json/personal_worktime_info_regular.json";

const pwInfo = pwInfoRegular.personalinfo;

const dailyData = [
  {
    date: "7/23",
    "4. とても満足": 40,
    "3. 満足": 24,
    "2. 不満": 24,
    "1. とても不満": 15,
    平均スコア: 4.1
  },
  {
    date: "7/24",
    "4. とても満足": 30,
    "3. 満足": 13,
    "2. 不満": 22,
    "1. とても不満": 30,
    平均スコア: 3.2
  },
  {
    date: "7/25",
    "4. とても満足": 20,
    "3. 満足": 98,
    "2. 不満": 22,
    "1. とても不満": 30,
    平均スコア: 2.3
  },
  {
    date: "7/26",
    "4. とても満足": 27,
    "3. 満足": 39,
    "2. 不満": 20,
    "1. とても不満": 18,
    平均スコア: 2.7
  },
  {
    date: "7/26",
    "4. とても満足": 18,
    "3. 満足": 48,
    "2. 不満": 21,
    "1. とても不満": 20,
    平均スコア: 1.8
  },
  {
    date: "7/27",
    "4. とても満足": 23,
    "3. 満足": 38,
    "2. 不満": 25,
    "1. とても不満": 3,
    平均スコア: 2.3
  },
  {
    date: "7/28",
    "4. とても満足": 34,
    "3. 満足": 43,
    "2. 不満": 21,
    "1. とても不満": 30,
    平均スコア: 3.4
  }
];

// カラーを用意する。
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];
// ラベルの位置や内容をカスタマイズ
const renderLabel = ({ name }: any) => {
  return name;
};

const WorkTimeBarChart = (): JSX.Element => {
  return (
    <div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}
        >
        <h2>あなたのワークスタイル</h2>
      </div>
      <ResponsiveContainer width="100%" height={400}>
        <ComposedChart width={800} height={200} data={pwInfo}>
          <XAxis dataKey="date" padding={{ right: 50, left: 50 }} />
          <YAxis
            yAxisId={1}
            label={{ value: "合計時間 [h]", angle: -90, dx: -20 }}
          />
          <YAxis
            yAxisId={2}
            orientation="right"
            domain={[0, 5]}
            tickCount={6}
            label={{ value: "全社平均 [h]", angle: 90, dx: 10 }}
          />
          <Tooltip />
          <Legend />
          <CartesianGrid strokeDasharray="3 3" />
          <Bar yAxisId={1} dataKey="meetingHours" name="会議"stackId="a" barSize={50} fill="#FF8042"
          />
          <Bar yAxisId={1} dataKey="devHours" name="開発（コーディング）" stackId="a" fill="#FFBB28" />
          <Bar yAxisId={1} dataKey="clericalWorkHours" name="事務作業" stackId="a" fill="#00C49F" />
          <Bar yAxisId={1} dataKey="seminarHours" name="セミナー" stackId="a" fill="#0088FE" />
          <Bar yAxisId={1} dataKey="trainingHours" name="研修" stackId="a" fill="#191970" />
          <Bar yAxisId={1} dataKey="breakTime" name="休憩" stackId="a" fill="#8a2be2" />
          <Line yAxisId={2} dataKey="同職種平均" />
        </ComposedChart>
      </ResponsiveContainer>
    </div>
  );
};
export default WorkTimeBarChart;
