import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import postProductAction from '../postProductAction';
import { Link } from "react-router-dom";

// UI
import Box from '@material-ui/core/Box';
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";

// Loading Icon
import Loader from 'react-loader-spinner'

// テスト用のjsonファイル
import ProductSample from '../json/products.json';
// DB サーバに投げる症状を分析するやつ
import { getsymptom } from '../getSymptom'
import { Description } from '@material-ui/icons';


const prodStyle = {
  figure: {
    width: '100%'
  },
  prodName: {
    'font-weight': 'bold',
    fontSize: '22px'
  },
  prodDesc: {
    fontSize: '18px',
  },
  prodPrice: {
    fontSize: '18px',
    'font-weight': 'bold'
  },
  prodRelease: {
    fontSize: '18px',
  },
  prodRegion: {
    fontSize: '14px',
  },
  prodNutrition: {
    fontSize: '14px',
  },
  prodAllergy: {
    fontSize: '14px',
  }
};

const buttonStyle = {
  fontSize: '14px',
  width: '100%'
};

const coutionStyle = {
  title: {
    'font-weight': 'bold',
    fontSize: '18px',
  },
  workStyle: {
    fontSize: '18px',
  },
  symptom: {
    fontSize: '22px',
    'font-weight': 'bold',
    color: '#ff0000'
  }
}





// Loading-Icon を表示させるフラグ
let loadingFlag = false
// Loadingしている風の時間 [msec]
const loadTime = 2000

// DBサーバ（APIサーバ）に投げる用のdata（post req.body）
const postData = {
  "symptom": "慢性的疲労"
}



// NULLハンドリング
// ・発売日
// ・商品説明
// ・栄養素（糖質、食物繊維）
const nullCheckDate = (date: string): string => {
  if (date == null) return "好評発売中"
  else {
    const fullDate: string = date.substr(0, 4) + "年" + date.substr(5, 2) + "月" + date.substr(8, 2)
    return fullDate
  }
}
const nullCheckDescription = (description: string): string => {
  if (description == null) return "ぜひお買い求めください。"
  else return description
}
const nullCheckNutrient = (nutrient: Number): Number => {
  if (nutrient == null) return 0
  else return nutrient
}
// DBから返ってくる配列を表示用に成形（insert comma）
// region, allergy
const insertCommaInArray = (array: Array<string>): string => {
  let withCommaString = ""
  let index
  for (index = 0; index < array.length - 1; index++)
    withCommaString += array[index] + ", "
  if (index == 0) withCommaString = "該当なし"
  else withCommaString += array[index]

  return withCommaString
}

// 消費税込み表示用変数
const includeTax = (price: number): number => {
  const tax = 10  //[%]
  const inTaxPrice: number = price * tax / 100
  return inTaxPrice
}

// 仮データ
//総合職（健康）
// symptoms: ["該当なし"]
// worstSymptom: "該当なし"
const result = {
  flagArray: {
    earlyFlag: true,
    lateFlag: true,
    longMeetingFlag: true,
    longDevFlag: true,
    irregularWorkingFlag: true,
    lackOfExerciseFlag: true
  },
  symptoms: ["睡眠不足", "腰痛", "眼精疲労"],
  worstSymptom: "睡眠不足"
}

const displayWorkStyleList = (flagArray: any): Array => {
  return (
    <div>

    </div>
  );

}


const RecommendProducts = (): JSX.Element => {
  const dispatch = useDispatch()
  const buttonAlert = async () => {
    loadingFlag = true
    await new Promise(resolve => setTimeout(resolve, loadTime)) //loadTimeだけ待つ
    dispatch(postProductAction(postData))
    loadingFlag = false
  }

  const state = useSelector((state: RootState) => state.postProductReducer)

  return (
    <div>

      <Grid container alignItems="center" justify="center">
        <Grid item xs>
          <Button onClick={buttonAlert} style={buttonStyle} size="small" variant="contained" color="primary">
            あなたのワークスタイルを分析
          </Button>
        </Grid>
      </Grid>

      <br />

      <Grid item xs={12} style={{ display: state.productShowFlag ? '' : 'none' }}>
        <p style={coutionStyle.title}>Caution!!<br />
        あなたは<br />
        〇〇
        なワークスタイルのようです。<br />
          このままでは、こんな症状の原因になりそう、、、</p>
        <p style={coutionStyle.symptom}>{insertCommaInArray(result.symptoms)}</p>
        <Link to="/HealthColumnPage">詳しく見る</Link>
      </Grid>


      <Grid container alignItems="center" justify="center">
        <Grid item xs={1} style={{ display: loadingFlag ? '' : 'none' }}>
          <Loader
            type="Audio"
            color="#191970"
            height={180}
            width={180}
            timeout={loadTime} //loadTimeだけ画面表示
          />
          {loadingFlag = true}
        </Grid>
      </Grid>


      <p style={{ fontSize: '25px', display: state.productShowFlag ? '' : 'none' }}>そんなあなたにオススメの商品は...</p>

      <Box>
        <Grid container spacing={1}
          style={{ border: '2px solid #000000', display: state.productShowFlag ? '' : 'none' }}>
          <Grid item xs style={{ display: state.productShowFlag ? '' : 'none' }}>
            <img src={state.figURL} style={prodStyle.figure} />
          </Grid>

          <Grid item xs style={{ display: state.productShowFlag ? '' : 'none' }}>
            <p style={prodStyle.prodName}>{state.prodName}</p>
            <p style={prodStyle.prodDesc}>{nullCheckDescription(state.description)}</p>
            <p style={prodStyle.prodPrice}>{state.price} 円（税抜）</p>
            <p style={prodStyle.prodRelease}>発売日：{nullCheckDate(state.date)}</p>
            <p style={prodStyle.prodRegion}>販売地域：{insertCommaInArray(state.region)}</p>
            <p style={prodStyle.prodNutrition}>熱量：{nullCheckNutrient(state.calory)} kcal, タンパク質：{nullCheckNutrient(state.protein)} g <br />
              脂質：{nullCheckNutrient(state.lipid)} g, 炭水化物：{nullCheckNutrient(state.carbonhydrates)} g<br />
              糖質：{nullCheckNutrient(state.sugar)} g, 食物繊維：{nullCheckNutrient(state.fiber)} g</p>
            <p style={prodStyle.prodNutrition}>食塩相当量：{nullCheckNutrient(state.scequiv)} g</p>
            <p style={prodStyle.prodAllergy}>本製品に含まれるアレルギー物質：{insertCommaInArray(state.allergy)}</p>
          </Grid>
        </Grid>
      </Box>

    </div>
  );
}

export default RecommendProducts;
